namespace Buckaroo\Magento2\Model\Config\Source;

class Categorylist implements \Magento\Framework\Option\ArrayInterface
{
    protected $categoryFactory;
    protected $categoryCollectionFactory;
    protected static $categoriesCache = [];

    public function __construct(
        \Magento\Catalog\Model\CategoryFactory $categoryFactory,
        \Magento\Catalog\Model\ResourceModel\Category\CollectionFactory $categoryCollectionFactory
    ) {
        $this->categoryFactory           = $categoryFactory;
        $this->categoryCollectionFactory = $categoryCollectionFactory;
    }

    public function getCategoryCollection($isActive = true, $level = false, $sortBy = false, $pageSize = false)
    {
        $collection = $this->categoryCollectionFactory->create();
        $collection->addAttributeToSelect('*');

        // select only active categories
        if ($isActive) {
            $collection->addIsActiveFilter();
        }

        // select categories of certain level
        if ($level) {
            $collection->addLevelFilter($level);
        }

        // sort categories by some value
        if ($sortBy) {
            $collection->addOrderField($sortBy);
        }

        // select certain number of categories
        if ($pageSize) {
            $collection->setPageSize($pageSize);
        }

        return $collection;
    }

    public function toOptionArray()
    {
        $arr = $this->_toArray();
        $ret = [];

        foreach ($arr as $key => $value) {
            $ret[] = [
                'value' => $key,
                'label' => $value,
            ];
        }

        return $ret;
    }

    private function _toArray()
    {
        $categories = $this->getCategoryCollection(true, false, false, false);

        $catagoryList = [];
        foreach ($categories as $category) {
            $catagoryList[$category->getEntityId()] = __(
                $this->_getParentName($category->getPath()) . $category->getName()
            );
        }

        return $catagoryList;
    }

    private function _getParentName($path = '')
    {
        $parentName = '';
        $rootCats   = [1, 2];

        $catTree = explode("/", $path);
        array_pop($catTree);

        if ($catTree && (count($catTree) > count($rootCats))) {
            $categoryObj = $this->categoryFactory->create();
            foreach ($catTree as $catId) {
                if (!in_array($catId, $rootCats)) {
                    if (!isset(self::$categoriesCache[$catId])) {
                        $category = $categoryObj->load($catId);
                        self::$categoriesCache[$catId] = $category->getName();
                    }
                    $parentName .= self::$categoriesCache[$catId] . ' -> ';
                }
            }
        }

        return $parentName;
    }
}
